INSERT INTO translations (language, key, text, created, created_by, modified, modified_by, user_created) VALUES ('en-US', 'administration.setup.biblivre3import.description', 'A Biblivre 3 installation was detected in this computer. You may wish to import these data for Biblivre 4, by selecting the items you are interested in, and pressing the button below. After importation, <strong>you will have to reindex the three bases: the Bibliographic one, and the ones for Authorities and Vocabulary </strong>, through the <strong>"Maintenance"</strong> screen in <strong>"Administration"</strong> and <strong>select again the permissions of employees with access to Biblivre</strong>, through the <strong>"Logins and Permissions"</strong> screen in <strong>"Administration"</strong>.', '2014-07-26 10:56:18.338867', 1, '2014-07-26 13:41:48.22945', 1, false);
